import Button from './Button';
import { useState, useEffect } from "react";

function Hello() {
  return <h1>Hello</h1>
}

function App() {
  const [showing, setShowing] = useState(false);
  const onClick = () => {setShowing((prev) => !prev)}
  return (
    <div className="App">
      <Button showing={showing} click={onClick} text='Show'/>
    </div>
  );
}

export default App;
