import Button from './Button';
import styles from "./App.module.css";
import { useState, useEffect } from "react";

function App() {
  const [value, setValue] = useState(0);
  const [keyword, setKeyword] = useState("");
  const onChange = (event) => {
    setKeyword(event.target.value);
  }
  const prevValue = () => {
    setValue((prev) => prev + 1)
    console.log("clicked")
  };
  // react를 import하여 React. 이 붙지않아도 사용가능

  console.log("state render.")

  useEffect(() => {
    console.log("call the api")
  }, []);
  
  console.log("Search For", keyword)
  
  // useEffect(() => {
  //   if(keyword !== "" && keyword.length >= 4){
  //     console.log("Search For", keyword)
  //   } // 이런식을 조건을 또 추가하는 것도 가능하다.
  // }, [keyword]);
  // []만 사용하면 무조건 한번만 실행된다.
  // 하지만, 내가 원하는건 keyword가 변화할 때만 코드를 실행시키는 것.
  // []에 keyword를 넣어주는 것은 'keyword'가 변화할 때 코드를 실행시키겠다고 react.js에게 알려주는 것

  // 그렇기에 빈 array []를 써주었을 때 코드가 단 한번만 실행된 이유이다.

  return (
    <div className="App">
      <div>
        <h1 className={styles.title}>Welcome to React!</h1>
        <Button text="Tour of CRA" />
      </div>

      <div>
        <h3>{value}</h3>
        <input 
          value={keyword} 
          onChange={onChange} 
          text="text" 
          placeholder='Search here...' 
        />
        <Button click={prevValue} text="click me!" />
      </div>
    </div>
  );
}

export default App;
