import Button from './Button';
import styles from "./App.module.css";
import { useState, useEffect } from "react";

function App() {
  const [value, setValue] = useState(0);
  const [keyword, setKeyword] = useState("");
  const onChange = (event) => {
    setKeyword(event.target.value);
  }
  const prevValue = () => setValue((prev) => prev + 1);
  // react를 import하여 React. 이 붙지않아도 사용가능
  // state가 변할 때마다 state에 해당되는 컴포넌트들은 모두 리랜더링된다.
  // 그래야 새로 수정된 UI를 볼 수 있기 때문.

  // 가끔은 처음 render할 때만 코드를 실행되도록 하고 싶은 것도 있을 것이다.
  // 예를들어 API를 호출할 때라던가.
  // 특정 코드의 실행을 제한하려면 어떻게 해야할까? -> useEffect

  // 첫 번째 argu: 딱 한번만 실행하고싶은 코드/함수..
  // 두 번쨰 argu: ??배열
  
  console.log("state render.")

  // const iRunOnlyOnce = () => {
  //   console.log("i run only once.")
  // };
  // useEffect(iRunOnlyOnce, []);

  useEffect(() => {
    console.log("call the api")
  }, []);
  // 처음에 불러오고 필요없이 실행될 코드를 낭비하지 않게 하기위해 사용한다.
  // 한번만 코드를 실행하거나 하고싶을 때 이렇게 쓸 수 있다.
  
  // console.log("Search For", keyword)
  // keyword가 변화할 때 뿐만아니라 counter가 변할 때도 search for.. 이 실행된다.
  useEffect(() => {
    if(keyword !== "" && keyword.length > 4){
      console.log("Search For", keyword)
    }
  }, [keyword]);
  // []만 사용하면 무조건 한번만 실행된다.
  // 하지만, 내가 원하는건 keyword가 변화할 때만 코드를 실행시키는 것.
  // []에 keyword를 넣어주는 것은 'keyword'가 변화할 때 코드를 실행시키겠다고 react.js에게 알려주는 것

  // 그렇기에 빈 array []를 써주었을 때 코드가 단 한번만 실행된 이유이다.

  return (
    <div className="App">
      <div>
        <h1 className={styles.title}>Welcome to React!</h1>
        <Button text="Tour of CRA" />
      </div>

      <div>
        <h3>{value}</h3>
        <input 
          value={keyword} 
          onChange={onChange} 
          text="text" 
          placeholder='Search here...' 
        />
        <Button click={prevValue} text="click me!" />
      </div>
    </div>
  );
}

export default App;
